import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/pages.css';

const API_BASE_URL = 'http://localhost:5000/api';

function OrderManagement() {
  const [orders, setOrders] = useState([]);
  const [activeTab, setActiveTab] = useState('pending');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [newOrderItems, setNewOrderItems] = useState([{ name: '', quantity: 1, price: 0 }]);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`${API_BASE_URL}/orders`);
      setOrders(response.data);
    } catch (err) {
      setError('Failed to fetch orders');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateOrder = async (e) => {
    e.preventDefault();
    
    // Validate items
    const validItems = newOrderItems.filter(item => item.name && item.quantity > 0 && item.price > 0);
    if (validItems.length === 0) {
      alert('Please add at least one valid item');
      return;
    }

    try {
      await axios.post(`${API_BASE_URL}/orders`, { items: validItems });
      setNewOrderItems([{ name: '', quantity: 1, price: 0 }]);
      setShowCreateForm(false);
      fetchOrders();
    } catch (err) {
      setError('Failed to create order');
      console.error(err);
    }
  };

  const handleItemChange = (index, field, value) => {
    const updatedItems = [...newOrderItems];
    updatedItems[index][field] = field === 'quantity' ? parseInt(value) : field === 'price' ? parseFloat(value) : value;
    setNewOrderItems(updatedItems);
  };

  const addItemField = () => {
    setNewOrderItems([...newOrderItems, { name: '', quantity: 1, price: 0 }]);
  };

  const removeItemField = (index) => {
    setNewOrderItems(newOrderItems.filter((_, i) => i !== index));
  };

  const filteredOrders = orders.filter(order => order.status === activeTab);

  return (
    <div className="page-container">
      <h2>Order Management</h2>
      
      {error && <div className="error-message">{error}</div>}

      <button 
        className="btn btn-primary"
        onClick={() => setShowCreateForm(!showCreateForm)}
      >
        {showCreateForm ? 'Cancel' : '+ Create New Order'}
      </button>

      {showCreateForm && (
        <form className="create-order-form" onSubmit={handleCreateOrder}>
          <h3>Create New Order</h3>
          
          <div className="form-section">
            <h4>Items</h4>
            {newOrderItems.map((item, index) => (
              <div key={index} className="item-input-group">
                <input
                  type="text"
                  placeholder="Item name"
                  value={item.name}
                  onChange={(e) => handleItemChange(index, 'name', e.target.value)}
                  className="input-field"
                />
                <input
                  type="number"
                  placeholder="Quantity"
                  min="1"
                  value={item.quantity}
                  onChange={(e) => handleItemChange(index, 'quantity', e.target.value)}
                  className="input-field"
                />
                <input
                  type="number"
                  placeholder="Price"
                  min="0"
                  step="0.01"
                  value={item.price}
                  onChange={(e) => handleItemChange(index, 'price', e.target.value)}
                  className="input-field"
                />
                {newOrderItems.length > 1 && (
                  <button
                    type="button"
                    className="btn btn-danger"
                    onClick={() => removeItemField(index)}
                  >
                    Remove
                  </button>
                )}
              </div>
            ))}
            <button
              type="button"
              className="btn btn-secondary"
              onClick={addItemField}
            >
              + Add Item
            </button>
          </div>

          <div className="form-actions">
            <button type="submit" className="btn btn-success">Create Order</button>
            <button type="button" className="btn btn-secondary" onClick={() => setShowCreateForm(false)}>Cancel</button>
          </div>
        </form>
      )}

      <div className="tabs">
        <button
          className={`tab ${activeTab === 'pending' ? 'active' : ''}`}
          onClick={() => setActiveTab('pending')}
        >
          Pending Orders ({filteredOrders.length})
        </button>
        <button
          className={`tab ${activeTab === 'completed' ? 'active' : ''}`}
          onClick={() => setActiveTab('completed')}
        >
          Completed Orders ({filteredOrders.length})
        </button>
      </div>

      <div className="orders-container">
        {loading && <div className="loading">Loading orders...</div>}
        
        {!loading && filteredOrders.length === 0 && (
          <div className="empty-state">
            <p>No {activeTab} orders</p>
          </div>
        )}

        {!loading && filteredOrders.length > 0 && (
          <div className="orders-list">
            {filteredOrders.map(order => (
              <div key={order.id} className={`order-card order-${order.status}`}>
                <div className="order-header">
                  <h3>{order.orderNumber}</h3>
                  <span className={`status-badge status-${order.status}`}>
                    {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                  </span>
                </div>

                <div className="order-items">
                  <h4>Items:</h4>
                  <ul>
                    {order.items.map((item, idx) => (
                      <li key={idx}>
                        {item.quantity}x {item.name} - ${(item.price * item.quantity).toFixed(2)}
                      </li>
                    ))}
                  </ul>
                </div>

                <div className="order-footer">
                  <div className="order-total">
                    <strong>Total: ${order.totalPrice.toFixed(2)}</strong>
                  </div>
                  <div className="order-time">
                    <small>Created: {new Date(order.createdAt).toLocaleString()}</small>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default OrderManagement;
