import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/pages.css';

const API_BASE_URL = 'http://localhost:5000/api';

function OrderManagement() {
  const [orders, setOrders] = useState([]);
  const [menu, setMenu] = useState({});
  const [activeTab, setActiveTab] = useState('pending');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('appetizers');
  const [searchTerm, setSearchTerm] = useState('');
  const [tableNumber, setTableNumber] = useState('');
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    fetchOrders();
    fetchMenu();
  }, []);

  const fetchOrders = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await axios.get(`${API_BASE_URL}/orders`);
      setOrders(response.data);
    } catch (err) {
      setError('Failed to fetch orders');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const fetchMenu = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/menu`);
      setMenu(response.data);
    } catch (err) {
      console.error('Failed to fetch menu', err);
    }
  };

  const categories = ['appetizers', 'mains', 'sides', 'desserts', 'drinks'];

  const tableGroups = {
    'Main Hall': Array.from({ length: 8 }, (_, i) => ({ id: i + 1, name: `MH${i + 1}` })),
    'Side Hall': Array.from({ length: 7 }, (_, i) => ({ id: i + 9, name: `SH${i + 9}` })),
    'Round Table': [{ id: 16, name: 'Round Table' }],
    'Garden': Array.from({ length: 9 }, (_, i) => ({ id: i + 17, name: `Garden${i + 1}` }))
  };

  const getFilteredMenuItems = () => {
    const items = menu[selectedCategory] || [];
    return items.filter(item =>
      item.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  const handleAddToCart = (item) => {
    const existingItem = cartItems.find(cartItem => cartItem.id === item.id);
    if (existingItem) {
      setCartItems(cartItems.map(cartItem =>
        cartItem.id === item.id
          ? { ...cartItem, quantity: cartItem.quantity + 1 }
          : cartItem
      ));
    } else {
      setCartItems([...cartItems, { ...item, quantity: 1 }]);
    }
  };

  const handleRemoveFromCart = (itemId) => {
    setCartItems(cartItems.filter(item => item.id !== itemId));
  };

  const handleUpdateQuantity = (itemId, quantity) => {
    if (quantity <= 0) {
      handleRemoveFromCart(itemId);
    } else {
      setCartItems(cartItems.map(item =>
        item.id === itemId ? { ...item, quantity } : item
      ));
    }
  };

  const handleCreateOrder = async (e) => {
    e.preventDefault();

    if (!tableNumber) {
      alert('Please enter a table number');
      return;
    }

    if (cartItems.length === 0) {
      alert('Please add items to the order');
      return;
    }

    try {
      await axios.post(`${API_BASE_URL}/orders`, {
        items: cartItems,
        tableNumber: parseInt(tableNumber)
      });
      setTableNumber('');
      setCartItems([]);
      setShowCreateForm(false);
      fetchOrders();
    } catch (err) {
      setError('Failed to create order');
      console.error(err);
    }
  };

  const cartTotal = cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);
  const filteredOrders = orders.filter(order => order.status === activeTab);

  return (
    <div className="page-container">
      <h2>Order Management</h2>

      {error && <div className="error-message">{error}</div>}

      <button
        className="btn btn-primary"
        onClick={() => setShowCreateForm(!showCreateForm)}
      >
        {showCreateForm ? 'Cancel' : '+ Create New Order'}
      </button>

      {showCreateForm && (
        <div className="create-order-section">
          <div className="menu-section">
            <div className="search-bar">
              <input
                type="text"
                placeholder="Search menu items..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
            </div>

            <div className="category-tabs">
              {categories.map(category => (
                <button
                  key={category}
                  className={`category-tab ${selectedCategory === category ? 'active' : ''}`}
                  onClick={() => setSelectedCategory(category)}
                >
                  {category.charAt(0).toUpperCase() + category.slice(1)}
                </button>
              ))}
            </div>

            <div className="menu-items-grid">
              {getFilteredMenuItems().map(item => (
                <div key={item.id} className="menu-item-card">
                  <h4>{item.name}</h4>
                  <p className="menu-item-price">${item.price.toFixed(2)}</p>
                  <button
                    className="btn btn-success btn-small"
                    onClick={() => handleAddToCart(item)}
                  >
                    + Add to Cart
                  </button>
                </div>
              ))}
            </div>
          </div>

          <div className="cart-section">
            <h3>Order Cart</h3>

            <div className="form-section">
              <label htmlFor="tableNumber">Table Number:</label>
              <input
                id="tableNumber"
                type="number"
                placeholder="Enter table number"
                value={tableNumber}
                onChange={(e) => setTableNumber(e.target.value)}
                className="input-field"
                min="1"
              />
            </div>

            {cartItems.length === 0 ? (
              <p className="empty-cart">No items in cart</p>
            ) : (
              <>
                <div className="cart-items">
                  {cartItems.map(item => (
                    <div key={item.id} className="cart-item">
                      <div className="cart-item-info">
                        <p className="cart-item-name">{item.name}</p>
                        <p className="cart-item-price">${item.price.toFixed(2)}</p>
                      </div>
                      <div className="cart-item-controls">
                        <button
                          className="btn-qty"
                          onClick={() => handleUpdateQuantity(item.id, item.quantity - 1)}
                        >
                          âˆ’
                        </button>
                        <span className="qty">{item.quantity}</span>
                        <button
                          className="btn-qty"
                          onClick={() => handleUpdateQuantity(item.id, item.quantity + 1)}
                        >
                          +
                        </button>
                        <button
                          className="btn btn-danger btn-small"
                          onClick={() => handleRemoveFromCart(item.id)}
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  ))}
                </div>

                <div className="cart-total">
                  <strong>Total: ${cartTotal.toFixed(2)}</strong>
                </div>

                <button
                  type="submit"
                  className="btn btn-success"
                  onClick={handleCreateOrder}
                  style={{ width: '100%' }}
                >
                  Place Order
                </button>
              </>
            )}
          </div>
        </div>
      )}

      <div className="tabs">
        <button
          className={`tab ${activeTab === 'pending' ? 'active' : ''}`}
          onClick={() => setActiveTab('pending')}
        >
          Pending Orders ({orders.filter(o => o.status === 'pending').length})
        </button>
        <button
          className={`tab ${activeTab === 'completed' ? 'active' : ''}`}
          onClick={() => setActiveTab('completed')}
        >
          Completed Orders ({orders.filter(o => o.status === 'completed').length})
        </button>
      </div>

      <div className="orders-container">
        {loading && <div className="loading">Loading orders...</div>}

        {!loading && filteredOrders.length === 0 && (
          <div className="empty-state">
            <p>No {activeTab} orders</p>
          </div>
        )}

        {!loading && filteredOrders.length > 0 && (
          <div className="orders-list">
            {filteredOrders.map(order => (
              <div key={order.id} className={`order-card order-${order.status}`}>
                <div className="order-header">
                  <div className="order-header-content">
                    <h3 className="table-number">Table {order.tableNumber}</h3>
                    <p className="order-number">{order.orderNumber}</p>
                  </div>
                  <span className={`status-badge status-${order.status}`}>
                    {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                  </span>
                </div>

                <div className="order-items">
                  <h4>Items:</h4>
                  <ul>
                    {order.items.map((item, idx) => (
                      <li key={idx}>
                        {item.quantity}x {item.name} - ${(item.price * item.quantity).toFixed(2)}
                      </li>
                    ))}
                  </ul>
                </div>

                <div className="order-footer">
                  <div className="order-total">
                    <strong>Total: ${order.totalPrice.toFixed(2)}</strong>
                  </div>
                  <div className="order-time">
                    <small>Created: {new Date(order.createdAt).toLocaleString()}</small>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default OrderManagement;
